    {
      "name": "start_interpolation",
      "trigger": "when 'moveTo' command received",
      "condition": "no active interpolation",
      "actions": [
        {"type": "set interpolatedPosition", 
         "params": {"value": "current position + (targetPosition - current position) * Time * interpolationSpeed"}}
      ]
    },
    {
      "name": "update_interpolation",
      "trigger": "every frame",
      "condition": "interpolation is active",
      "actions": [
        {"type": "set interpolatedPosition", 
         "params": {"value": "current position + (targetPosition - current position) * Time * interpolationSpeed"}}
      ]
    },
    {
      "name": "complete_interpolation",
      "trigger": "when interpolatedPosition is within 0.01 of targetPosition",
      "condition": true,
      "actions": [
        {"type": "set position to targetPosition"},
        {"type": "stop_interpolation"}
      ]
    }

