using UnityEngine;
using System.Collections;

public class SpinUpExplosion : MonoBehaviour {
    [SerializeField] private float startSpeed = 0.1f;
    [SerializeField] private float maxSpeed = 50.0f;
    [SerializeField] private float spinUpDuration = 2.0f;

    [SerializeField] private bool explodeOnMaxSpin = true;
    [SerializeField] private float explosionRadius = 3.0f;
    [SerializeField] private int fragmentCount = 10;
    [SerializeField] private float[] fragmentSpeedRange = { 5, 20 };
    [SerializeField] private float[] fragmentScaleRange = { 0.3f, 0.8f };

    [SerializeField] private AudioClip explosionSound;

    private Rigidbody rb;
    private Vector3 initialSpinDirection;
    private float currentSpeed;
    private float maxSpinTimer;

    // Start is called before the first frame update
    void Start() {
        rb = GetComponent<Rigidbody>();
        if (rb == null) {
            Debug.LogWarning("Rigidbody component not found on SpinUpExplosion!");
        }

        if (explodeOnMaxSpin && explosionSound == null) {
            Debug.LogWarning("Audio source required for explosion sound!");
        }
    }

    // Update is called once per frame
    void Update() {
        currentSpeed = rb.velocity.magnitude;
        maxSpinTimer += Time.deltaTime;

        float progress = Mathf.Clamp01(maxSpinTimer / spinUpDuration);
        float targetSpeed = Mathf.Lerp(startSpeed, maxSpeed, progress);

        rb.angularVelocity = initialSpinDirection * targetSpeed;

        if (explodeOnMaxSpin && currentSpeed >= maxSpeed) {
            PerformExplosion();
        }
    }

    private void PerformExplosion() {
        Destroy(gameObject); // Destroy original object

        Instantiate(explosionSound, transform.position, Quaternion.identity);

        Vector3 spawnPos = transform.position;
        for (int i = 0; i < fragmentCount; i++) {
            float randomSpeed = Random.Range(fragmentSpeedRange[0], fragmentSpeedRange[1]);
            float randomScale = Random.Range(fragmentScaleRange[0], fragmentScaleRange[1]);

            GameObject explosionFragment = Instantiate(gameObject, spawnPos, Quaternion.identity);
            explosionFragment.transform.localScale *= randomScale;
            explosionFragment.GetComponent<Rigidbody>().velocity = transform.forward * randomSpeed;

            Vector3 randomSpinDirection = Random.insideUnitSphere.normalized;
            explosionFragment.GetComponent<ExplosionFragment>().initialSpinDirection = randomSpinDirection;
        }
    }

    public void PlaySpinUpAnimation() {
        initialSpinDirection = transform.forward;
        rb.angularDrag = 0f; // Disable drag for smooth spin
        StartCoroutine(SpinUpCoroutine());
    }

    private IEnumerator SpinUpCoroutine() {
        yield return new WaitForSeconds(0.1f); // Delay before starting spin

        float currentTimer = 0f;

        while (currentTimer < spinUpDuration) {
            currentTimer += Time.deltaTime;
            float progress = Mathf.Clamp01(currentTimer / spinUpDuration);
            float targetSpeed = Mathf.Lerp(startSpeed, maxSpeed, progress);

            rb.angularVelocity = initialSpinDirection * targetSpeed;

            yield return null; // Yield to update
        }
    }

}
