using UnityEngine;
using System.Collections.Generic;

public class RobotCompanion : MonoBehaviour
{
    [Header("Robot Settings")]
    [SerializeField] private string _robotName = "Sentinel";
    [SerializeField] private float _detectionRange = 20f;
    [SerializeField] private LayerMask _obstacleLayer = -1;
    [SerializeField] private float _movementSpeed = 3f;
    [SerializeField] private float _rotationSpeed = 90f;
    [SerializeField] private float _rechargeHeight = 2f;

    [Header("Visual Settings")]
    [SerializeField] private ParticleSystem _powerUpParticles;
    [SerializeField] private AudioSource _audioSource;
    [SerializeField] private AudioClip[] _modeSounds;
    [SerializeField] private AudioClip[] _servoSounds;
    [SerializeField] private AudioClip[] _alertSounds;
    [SerializeField] private AudioClip[] _defensiveSounds;
    [SerializeField] private AudioClip[] _detectionSounds;

    [Header("Modes")]
    public enum RobotMode
    {
        Standby,
        Following,
        Alert,
        Defensive,
        Recharging,
        Damaged
    }

    private RobotMode _currentMode = RobotMode.Standby;
    private Transform _player;
    private Vector3 _rechargePosition;
    private Rigidbody _rigidbody;
    private float _rechargeTimer = 0f;
    private List<GameObject> _obstacles = new List<GameObject>();

    private void Start()
    {
        _rigidbody = GetComponent<Rigidbody>();
        if (_audioSource == null)
            _audioSource = GetComponent<AudioSource>();

        if (_player == null)
            _player = GameObject.FindGameObjectWithTag("Player").transform;

        if (_rechargePosition == default(Vector3))
        {
            _rechargePosition = transform.position + Vector3.up * _rechargeHeight;
        }

        _rechargeTimer = Random.Range(0f, 5f);
    }

    private void Update()
    {
        switch (_currentMode)
        {
            case RobotMode.Standby:
                Standby();
                break;

            case RobotMode.Following:
                FollowPlayer();
                break;

            case RobotMode.Alert:
                DetectThreats();
                break;

            case RobotMode.Defensive:
                ProjectShield();
                break;

            case RobotMode.Recharging:
                Recharge();
                break;

            case RobotMode.Damaged:
                ShowDamageEffects();
                break;
        }
    }

    private void Standby()
    {
        UpdateGlowColor(_currentMode == RobotMode.Defensive ? Color.red : Color.blue);
        PlaySound(_modeSounds[0]);

        if (_player != null)
        {
            float distance = Vector3.Distance(transform.position, _player.position);
            if (distance <= 1f)
                switchToFollowing();
        }
    }

    private void FollowPlayer()
    {
        UpdateGlowColor(Color.green);
        PlaySound(_servoSounds[Random.Range(0, _servoSounds.Length)]);

        if (_player == null) return;

        Vector3 direction = (_player.position - transform.position).normalized;
        direction.y = 0f; // Prevent vertical movement

        Vector3 movePosition = transform.position + direction * _movementSpeed * Time.deltaTime;
        transform.position = Vector3.Lerp(transform.position, movePosition, Time.deltaTime);
        transform.LookAt(_player.position);

        if (Vector3.Distance(transform.position, _rechargePosition) <= 0.5f)
            switchToRecharging();
    }

    private void DetectThreats()
    {
        UpdateGlowColor(Color.yellow);
        PlaySound(_detectionSounds[Random.Range(0, _detectionSounds.Length)]);

        if (_player == null) return;

        float distance = Vector3.Distance(transform.position, _player.position);
        bool hasObstacleInFront = false;

        foreach (GameObject obstacle in _obstacles)
        {
            float obstacleDistance = Vector3.Distance(transform.position, obstacle.transform.position);
            float angleToObstacle = Vector3.Angle(transform.forward, (obstacle.transform.position - transform.position).normalized);

            if (angleToObstacle <= 45f && obstacleDistance < distance + 1f)
            {
                hasObstacleInFront = true;
                break;
            }
        }

        if (hasObstacleInFront || distance > _detectionRange)
        {
            switchToAlert();
        }
    }

    private void ProjectShield()
    {
        UpdateGlowColor(Color.red);
        PlaySound(_defensiveSounds[Random.Range(0, _defensiveSounds.Length)]);

        CreatePowerUpParticles();

        if (_player == null) return;

        float distance = Vector3.Distance(transform.position, _player.position);
        bool hasObstacleInFront = false;

        foreach (GameObject obstacle in _obstacles)
        {
            float obstacleDistance = Vector3.Distance(transform.position, obstacle.transform.position);
            float angleToObstacle = Vector3.Angle(transform.forward, (obstacle.transform.position - transform.position).normalized);

            if (angleToObstacle <= 45f && obstacleDistance < distance + 1f)
            {
                hasObstacleInFront = true;
                break;
            }
        }

        if (hasObstacleInFront || distance > _detectionRange)
        {
            switchToAlert();
        }
    }

    private void Recharge()
    {
        UpdateGlowColor(Color.orange);
        PlaySound(_modeSounds[1]);

        float distance = Vector3.Distance(transform.position, _rechargePosition);
        if (distance <= 0.5f)
        {
            _rechargeTimer -= Time.deltaTime * 2f;

            if (_rechargeTimer <= 0f)
            {
                switchToStandby();
            }
        }
    }

    private void ShowDamageEffects()
    {
        UpdateGlowColor(Color.red);
        PlaySound(_alertSounds[Random.Range(0, _alertSounds.Length)]);

        CreatePowerUpParticles();

        if (_player == null) return;

        float distance = Vector3.Distance(transform.position, _player.position);
        bool hasObstacleInFront = false;

        foreach (GameObject obstacle in _obstacles)
        {
            float obstacleDistance = Vector3.Distance(transform.position, obstacle.transform.position);
            float angleToObstacle = Vector3.Angle(transform.forward, (obstacle.transform.position - transform.position).normalized);

            if (angleToObstacle <= 45f && obstacleDistance < distance + 1f)
            {
                hasObstacleInFront = true;
                break;
            }
        }

        if (!hasObstacleInFront && distance > _detectionRange)
        {
            switchToAlert();
        }
    }

    private void OnTriggerEnter(Collider other)
    {
        if (other.CompareTag("Player"))
        {
            switchToFollowing();
        }
    }

    private void OnTriggerExit(Collider other)
    {
        if (other.CompareTag("Player"))
        {
            switchToStandby();
        }
    }

    private void CreatePowerUpParticles()
    {
        if (_powerUpParticles != null && _powerUpParticles.isPlaying)
        {
            GameObject powerUp = new GameObject($"Power Up - {Time.timeToString()}");
            powerUp.transform.SetParent(transform);

            Collider col = powerUp.AddComponent<Collider>();
            col.isTrigger = true;
            col.boundsSize = Vector3.one * 0.5f;
            col.center = transform.position;

            var particleSystem = powerUp.AddComponent<ParticleSystem>();
            if (particleSystem != null)
            {
                ParticleSystem.DefaultPrefab = _powerUpParticles?.gameObject;
                particleSystem.Play();
            }
        }
    }

    private void UpdateGlowColor(Color targetColor)
    {
        Renderer renderer = GetComponent<Renderer>();
        if (renderer != null)
        {
            Color currentColor = renderer.material.color;
            currentColor.a = 0.5f; // Add some transparency

            switch (targetColor)
            {
                case Color.red:
                    currentColor += Color.red * 0.3f;
                    break;
                case Color.yellow:
                    currentColor += Color.yellow * 0.7f;
                    break;
                case Color.green:
                    currentColor += Color.green * 1f;
                    break;
                case Color.blue:
                    currentColor += Color.blue * 1f;
                    break;
            }

            renderer.material.color = currentColor;
        }
    }

    private void PlaySound(AudioClip clip)
    {
        if (_audioSource != null && clip != null)
        {
            _audioSource.PlayOneShot(clip);
        }
    }

    public void SwitchToMode(RobotMode newMode)
    {
        _currentMode = newMode;

        switch (newMode)
        {
            case RobotMode.Following:
                if (_player == null) return;
                StartCoroutine(FollowPlayerCoroutine());
                break;

            case RobotMode.Recharging:
                StartCoroutine(RechargeCoroutine());
                break;

            case RobotMode.Damaged:
                StartCoroutine(DamageCoroutine());
                break;
        }
    }

    private IEnumerator FollowPlayerCoroutine()
    {
        while (true)
        {
            yield return new WaitForSeconds(2f);
            switchToStandby();
        }
    }

    private IEnumerator RechargeCoroutine()
    {
        _rechargeTimer = Random.Range(0f, 5f);

        while (_rechargeTimer > 0f)
        {
            yield return null;
            _rechargeTimer -= Time.deltaTime * 2f;
        }

        switchToStandby();
    }

    private IEnumerator DamageCoroutine()
    {
        float originalSpeed = _movementSpeed;
        float originalRotationSpeed = _rotationSpeed;

        _movementSpeed *= 0.5f;
        _rotationSpeed *= 0.5f;

        yield return new WaitForSeconds(3f);

        _movementSpeed = originalSpeed;
        _rotationSpeed = originalRotationSpeed;
    }

    public void AddObstacle(GameObject obstacle)
    {
        if (!_obstacles.Contains(obstacle))
            _obstacles.Add(obstacle);
    }

    public void RemoveObstacle(GameObject obstacle)
    {
        _obstacles.Remove(obstacle);
    }

    public string GetRobotName()
    {
        return _robotName;
    }

    private void OnDrawGizmosSelected()
    {
        Gizmos.color = Color.yellow;
        Gizmos.DrawWireSphere(transform.position, _detectionRange);

        if (_player != null)
        {
            Gizmos.color = Color.green;
            Gizmos.DrawLine(transform.position, _player.position);
        }
    }
}
